# coding=utf-8import matplotlib.pyplot as pltimport numpy as npimport torchfrom meta_info import *result_root_this_script = join(results_root, 'Chapter5/statistic')class Dataframe_func:    def __init__(self,df,is_clean_df=True):        print('add lon lat')        df = self.add_lon_lat(df)        print('add landcover')        df = self.add_GLC_landcover_data_to_df(df)        print('add NDVI mask')        df = self.add_NDVI_mask(df)        print('add Aridity Index')        df = self.add_AI_to_df(df)        print('add ELI')        df = self.add_ELI_to_df(df)        print('add ELI_reclass')        df = self.ELI_reclass(df)        print('add AI_reclass')        df = self.AI_reclass(df)        # print('add ELI_significance')        # df = self.add_ELI_significance(df)        print('add koppen')        df = self.add_koppen(df)        if is_clean_df == True:            df = self.clean_df(df)        self.df = df    def clean_df(self,df):        df = df[df['lat']>=30]        # df = df[df['landcover_GLC'] != 'Crop']        df = df[df['NDVI_MASK'] == 1]        # df = df[df['ELI_significance'] == 1]        return df    def add_GLC_landcover_data_to_df(self, df):        f = join(data_root,'GLC2000/reclass_lc_dic.npy')        val_dic=T.load_npy(f)        val_list = []        for i, row in tqdm(df.iterrows(), total=len(df)):            pix = row['pix']            if not pix in val_dic:                val_list.append(np.nan)                continue            vals = val_dic[pix]            val_list.append(vals)        df['landcover_GLC'] = val_list        return df    def add_NDVI_mask(self,df):        # f =rf'C:/Users/pcadmin/Desktop/Data/Base_data/NDVI_mask.tif'        f = join(data_root, 'GIMMS_NDVI/NDVI_mask.tif')        print(f)        array, originX, originY, pixelWidth, pixelHeight = ToRaster().raster2array(f)        array = np.array(array, dtype=float)        val_dic = DIC_and_TIF().spatial_arr_to_dic(array)        f_name = 'NDVI_MASK'        print(f_name)        # exit()        val_list = []        for i, row in tqdm(df.iterrows(), total=len(df)):            pix = row['pix']            if not pix in val_dic:                val_list.append(np.nan)                continue            vals = val_dic[pix]            if vals < -99:                val_list.append(np.nan)                continue            val_list.append(vals)        df[f_name] = val_list        return df    def add_AI_to_df(self, df):        f = join(data_root, 'Aridity_Index/aridity_index.tif')        spatial_dict = DIC_and_TIF().spatial_tif_to_dic(f)        df = T.add_spatial_dic_to_df(df, spatial_dict, 'aridity_index')        return df    def add_lon_lat(self,df):        df = T.add_lon_lat_to_df(df, DIC_and_TIF())        return df    def add_ELI_to_df(self,df):        from Chapter5 import analysis        f = join(analysis.Water_energy_limited_area().this_class_tif, 'ELI/GLEAM-ET_ERA-SM_Temperature.tif')        spatial_dict = DIC_and_TIF().spatial_tif_to_dic(f)        df = T.add_spatial_dic_to_df(df, spatial_dict, 'ELI')        return df    def ELI_reclass(self,df):        ELI_class = []        for i, row in tqdm(df.iterrows(), total=len(df)):            ELI = row['ELI']            if ELI < 0:                ELI_class.append('Energy-Limited')            elif ELI > 0:                ELI_class.append('Water-Limited')            else:                ELI_class.append(np.nan)        df['ELI_class'] = ELI_class        return df    def AI_reclass(self,df):        AI_class = []        for i,row in df.iterrows():            AI = row['aridity_index']            if AI < 0.65:                AI_class.append('Arid')            elif AI >= 0.65:                AI_class.append('Humid')            elif np.isnan(AI):                AI_class.append(np.nan)            else:                print(AI)                raise ValueError('AI error')        df['AI_class'] = AI_class        return df    def add_koppen(self,df):        f = join(data_root, 'koppen/koppen_reclass_dic.npy')        val_dic = T.load_npy(f)        df = T.add_spatial_dic_to_df(df, val_dic, 'Koppen')        return df    # def add_ELI_significance(self,df):    #     from Chapter5 import analysis    #     f = join(Water_energy_limited_area().this_class_tif, 'significant_pix_r/ELI_Temp_significance.tif')    #     spatial_dict = DIC_and_TIF().spatial_tif_to_dic(f)    #     df = T.add_spatial_dic_to_df(df, spatial_dict, 'ELI_significance')    #    #     return dfclass Dataframe:    def __init__(self):        self.this_class_arr, self.this_class_tif, self.this_class_png = \            T.mk_class_dir('Dataframe', result_root_this_script, mode=2)        self.dff = join(self.this_class_arr, 'dataframe.df')        pass    def run(self):        df = self.copy_df()        df = self.__gen_df_init()        df = Dataframe_func(df).df        T.save_df(df, self.dff)        T.df_to_excel(df, self.dff)        pass    def __gen_df_init(self):        if not os.path.isfile(self.dff):            df = pd.DataFrame()            T.save_df(df,self.dff)            return df        else:            df,dff = self.__load_df()            return df    def __load_df(self):        dff = self.dff        df = T.load_df(dff)        T.print_head_n(df)        print('len(df):',len(df))        return df,dff    def copy_df(self):        from Chapter5 import analysis        dff = analysis.Resistance_Resilience().dff        df = T.load_df(dff)        T.save_df(df, self.dff)        T.df_to_excel(df, self.dff)        return dfclass Hot_Normal_Rs_Rt:    '''    Rs/Rt in water limited and energy limited area under Normal/hot droughts    '''    def __init__(self):        self.this_class_arr, self.this_class_tif, self.this_class_png = \            T.mk_class_dir('Hot_Normal_Rs_Rt', result_root_this_script, mode=2)        self.drought_type_list = ['normal-drought', 'hot-drought']        pass    def run(self):        # self.rs_rt_tif()        # self.plot_rs_rt_spatial()        #        # self.rs_rt_bar()        # self.rs_rt_hist()        # self.rs_rt_bar_water_energy_limited()        # self.rs_rt_bar_Humid_Arid()        # self.rs_rt_bar_PFTs()        # self.rs_rt_pfts_koppen_scatter()        # self.rs_rt_area_ratio_bar()        # self.rs_rt_area_ratio_bar_two_regions()        self.rs_rt_area_ratio_ELI_matrix()        # self.rs_rt_pfts_koppen_area_ratio_scatter()        pass    def rs_rt_tif(self):        outdir = join(self.this_class_tif, 'rs_rt_tif')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        cols = get_rs_rt_cols()        T.print_head_n(df, 5)        drought_type_list = T.get_df_unique_val_list(df, 'drought_type')        for drt in drought_type_list:            df_drt = df[df['drought_type'] == drt]            outdir_i = join(outdir, drt)            T.mk_dir(outdir_i)            for col in cols:                print(col)                outf = join(outdir_i, '{}_{}.tif'.format(drt,col))                spatial_dict = {}                df_group_dict = T.df_groupby(df_drt, 'pix')                for pix in df_group_dict:                    df_pix = df_group_dict[pix]                    vals = df_pix[col]                    vals = np.array(vals)                    mean = np.nanmean(vals)                    spatial_dict[pix] = mean                DIC_and_TIF().pix_dic_to_tif(spatial_dict, outf)    def plot_rs_rt_spatial(self):        fdir = join(self.this_class_tif, 'rs_rt_tif')        outdir = join(self.this_class_png,'rs_rt_spatial')        T.mk_dir(outdir)        T.open_path_and_file(outdir)        for drt in self.drought_type_list:            fdir_i = join(fdir, drt)            outdir_i = join(outdir, drt)            T.mk_dir(outdir_i)            for f in T.listdir(fdir_i):                if not f.endswith('.tif'):                    continue                fpath = join(fdir_i, f)                plt.figure(figsize=(5, 5))                m,ret = Plot().plot_ortho(fpath, vmin=0.95,vmax=1.05,cmap=global_cmap)                m.colorbar(ret, location='bottom', pad="5%")                plt.title(f.replace('.tif',''))                outf = join(outdir_i, f.replace('.tif', '.png'))                # plt.show()                plt.savefig(outf,dpi=600)                plt.close()        pass    def rs_rt_bar(self):        outdir = join(self.this_class_png, 'rs_rt_bar')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        cols = get_rs_rt_cols()        y_list = []        x_list = []        err_list = []        for col in cols:            for drt in self.drought_type_list:                df_drt = df[df['drought_type'] == drt]                # print(col)                vals = df_drt[col]                vals = np.array(vals)                mean = np.nanmean(vals)                std = np.nanstd(vals)                err,_,_ = T.uncertainty_err(vals)                y_list.append(mean)                err_list.append(err)                tick = '{}_{}'.format(drt, col)                x_list.append(tick)        plt.figure(figsize=(6,3))        plt.bar(x_list, y_list, yerr=err_list)        plt.xticks(rotation=90)        plt.ylim(0.97, 1.01)        plt.tight_layout()        plt.show()        # outf = join(outdir, 'rs_rt_bar.pdf')        # plt.savefig(outf, dpi=300)    def rs_rt_bar_water_energy_limited(self):        outdir = join(self.this_class_png, 'rs_rt_bar_water_energy_limited')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        cols = get_rs_rt_cols()        limited_area_list = T.get_df_unique_val_list(df, 'ELI_class')        for ltd in limited_area_list:            df_ltd = df[df['ELI_class'] == ltd]            y_list = []            x_list = []            err_list = []            for col in cols:                for drt in self.drought_type_list:                    df_drt = df_ltd[df_ltd['drought_type'] == drt]                    # print(col)                    vals = df_drt[col]                    vals = np.array(vals)                    mean = np.nanmean(vals)                    std = np.nanstd(vals)                    err,_,_ = T.uncertainty_err(vals)                    y_list.append(mean)                    err_list.append(err)                    tick = '{}_{}'.format(drt, col)                    x_list.append(tick)            plt.figure(figsize=(6,3))            plt.bar(x_list, y_list, yerr=err_list)            plt.xticks(rotation=90)            plt.ylim(0.90, 1.01)            plt.title(ltd)            plt.tight_layout()            # plt.show()            outf = join(outdir, f'{ltd}-rs_rt_bar.png')            plt.savefig(outf, dpi=300)            plt.close()    def rs_rt_bar_Humid_Arid(self):        outdir = join(self.this_class_png, 'rs_rt_bar_Humid_Arid')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        cols = get_rs_rt_cols()        limited_area_list = T.get_df_unique_val_list(df, 'AI_class')        for ltd in limited_area_list:            df_ltd = df[df['AI_class'] == ltd]            y_list = []            x_list = []            err_list = []            for col in cols:                for drt in self.drought_type_list:                    df_drt = df_ltd[df_ltd['drought_type'] == drt]                    # print(col)                    vals = df_drt[col]                    vals = np.array(vals)                    mean = np.nanmean(vals)                    std = np.nanstd(vals)                    err,_,_ = T.uncertainty_err(vals)                    y_list.append(mean)                    err_list.append(err)                    tick = '{}_{}'.format(drt, col)                    x_list.append(tick)            plt.figure(figsize=(6,3))            plt.bar(x_list, y_list, yerr=err_list)            plt.xticks(rotation=90)            plt.ylim(0.90, 1.01)            plt.title(ltd)            plt.tight_layout()            # plt.show()            outf = join(outdir, f'{ltd}-rs_rt_bar.png')            plt.savefig(outf, dpi=300)    def rs_rt_bar_PFTs(self):        outdir = join(self.this_class_png, 'rs_rt_bar_PFTs')        T.mk_dir(outdir)        T.open_path_and_file(outdir)        df = Load_dataframe().load_chapter5()        cols = get_rs_rt_cols()        limited_area_list = T.get_df_unique_val_list(df, 'landcover_GLC')        for ltd in limited_area_list:            df_ltd = df[df['landcover_GLC'] == ltd]            y_list = []            x_list = []            err_list = []            for col in cols:                for drt in self.drought_type_list:                    df_drt = df_ltd[df_ltd['drought_type'] == drt]                    # print(col)                    vals = df_drt[col]                    vals = np.array(vals)                    mean = np.nanmean(vals)                    std = np.nanstd(vals)                    err,_,_ = T.uncertainty_err(vals)                    y_list.append(mean)                    err_list.append(err)                    tick = '{}_{}'.format(drt, col)                    x_list.append(tick)            plt.figure(figsize=(6,3))            plt.bar(x_list, y_list, yerr=err_list)            plt.xticks(rotation=90)            plt.ylim(0.90, 1.01)            plt.title(ltd)            plt.tight_layout()            # plt.show()            outf = join(outdir, f'{ltd}-rs_rt_bar.png')            plt.savefig(outf, dpi=300)            plt.close()    def rs_rt_hist(self):        outdir = join(self.this_class_png, 'rs_rt_hist')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        cols = get_rs_rt_cols()        # DIC_and_TIF().plot_df_spatial_pix(df,land_tif)        # plt.show()        # print(cols)        # exit()        for drt in self.drought_type_list:            flag = 0            for col in cols:                df_drt = df[df['drought_type'] == drt]                # print(col)                vals = df_drt[col]                vals = np.array(vals)                x,y = Plot().plot_hist_smooth(vals, bins=200, alpha=0, range=(0.8,1.2))                y = y + flag * 0.03                if 'hot' in drt:                    alpha = 0.5                else:                    alpha = 1                plt.plot(x,y,label=f'{drt} {col}', alpha=alpha)                flag += 1        plt.legend()        # plt.title(drt)        plt.show()        # outf = join(outdir, '{}.png'.format(col))        # plt.savefig(outf, dpi=300)        # plt.close()    def rs_rt_pfts_koppen_scatter(self):        outdir = join(self.this_class_png, 'rs_rt_pfts_koppen_scatter')        T.mk_dir(outdir)        # rs_col = 'rt'        # rs_col = 'ELI'        rs_col = 'rs_1'        # rs_col = 'rs_2'        # rs_col = 'rs_3'        # rs_col = 'rs_4'        eli_col = 'ELI'        # eli_col = 'max_scale'        # eli_col = 'max_lag'        lc_col = 'landcover_GLC'        koppen_col = 'Koppen'        df = Load_dataframe().load_chapter5()        drought_type_list = global_drought_type_list        lc_list = global_lc_list        koppen_list = global_koppen_list        lc_marker_dict = global_lc_marker_dict        koppen_color_dict = global_koppen_color_dict        for drt in drought_type_list:            df_drt = df[df['drought_type'] == drt]            plt.figure()            xx = []            yy = []            for lc in lc_list:                df_lc = df_drt[df_drt[lc_col] == lc]                for kp in koppen_list:                    df_kp = df_lc[df_lc[koppen_col] == kp]                    if len(df_kp) <= 100:                        continue                    x = df_kp[eli_col]                    y = df_kp[rs_col]                    x = np.array(x)                    y = np.array(y)                    x_err = T.uncertainty_err(x)[0]                    y_err = T.uncertainty_err(y)[0]                    # x_err = np.nanstd(x)                    # y_err = np.nanstd(y)                    x_mean = np.nanmean(x)                    y_mean = np.nanmean(y)                    xx.append(x_mean)                    yy.append(y_mean)                    plt.errorbar(x_mean, y_mean, xerr=x_err, yerr=y_err,color='gray', alpha=0.5,zorder=-99)                    plt.scatter(x_mean, y_mean, marker=lc_marker_dict[lc], color=koppen_color_dict[kp], label=f'{kp}-{lc}',edgecolors='k',zorder=0)            # plt.legend()            sns.regplot(xx, yy, scatter=False, color='gray')            plt.title(drt)            plt.xlabel(eli_col)            plt.ylabel(rs_col)            # plt.ylim(0.91, 1.02)            # outf = join(outdir, f'{drt}-{eli_col}-{rs_col}-scatter.png')            outf = join(outdir, f'{drt}-{eli_col}-{rs_col}-scatter.pdf')            plt.savefig(outf, dpi=300)            plt.close()        plt.show()    def rs_rt_pfts_koppen_area_ratio_scatter(self):        outdir = join(self.this_class_png, 'rs_rt_pfts_koppen_area_ratio_scatter')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        threshold = 0.05        rs_cols = get_rs_rt_cols()        drought_type_list = global_drought_type_list        lc_list = global_lc_list        koppen_list = global_koppen_list        eli_col = 'ELI'        for drt in drought_type_list:            df_drt = df[df['drought_type'] == drt]            for rs_col in rs_cols:                plt.figure()                xx = []                yy = []                for lc in lc_list:                    df_lc = df_drt[df_drt['landcover_GLC'] == lc]                    for kp in koppen_list:                        df_kp = df_lc[df_lc['Koppen'] == kp]                        df_kp_copy = df_kp.copy()                        df_kp_copy = df_kp_copy.dropna(subset=[eli_col, rs_col], how='any')                        vals = df_kp_copy[rs_col]                        vals = np.array(vals)                        vals = vals[vals < (1 - threshold)]                        # vals = vals[vals > (1 + threshold)]                        ratio = len(vals) / len(df_kp_copy) * 100                        x = df_kp_copy[eli_col]                        x = np.array(x)                        x_mean = np.nanmean(x)                        xx.append(x_mean)                        yy.append(ratio)                        plt.scatter(x_mean, ratio, marker=global_lc_marker_dict[lc], color=global_koppen_color_dict[kp],                                    label=f'{kp}-{lc}', edgecolors='k', zorder=0, s=100)                plt.legend()                sns.regplot(xx, yy, scatter=False, color='gray')                plt.title(f'{drt} {rs_col}')                # outf = join(outdir, f'{drt}-{rs_col}-area_ratio_scatter.png')                # outf = join(outdir, f'{drt}-{rs_col}-area_ratio_scatter.pdf')                outf = join(outdir, f'legend.pdf')                plt.savefig(outf, dpi=300)                plt.close()                exit()                # plt.show()                # plt.ylim(-0.3,0.7)            # plt.show()    def rs_rt_area_ratio_bar(self):        outdir = join(self.this_class_png, 'rs_rt_area_ratio_bar')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        threshold = 0.05        rs_cols = get_rs_rt_cols()        drought_type_list = global_drought_type_list        ELI_class_list = global_ELI_class_list        data = pd.DataFrame()        drt_list = []        rs_col_list = []        ratio_list = []        for drt in drought_type_list:            df_drt = df[df['drought_type'] == drt]            for rs_col in rs_cols:                df_drt_copy = df_drt.copy()                df_drt_copy = df_drt_copy.dropna(subset=['ELI', rs_col], how='any')                vals = df_drt_copy[rs_col]                vals = np.array(vals)                vals = vals[vals < (1 - threshold)]                # vals = vals[vals > (1 + threshold)]                ratio = len(vals) / len(df_drt_copy) * 100                drt_list.append(drt)                rs_col_list.append(rs_col)                ratio_list.append(ratio)        data['drought_type'] = drt_list        data['rs_col'] = rs_col_list        data['ratio'] = ratio_list        # sns.pointplot(x='rs_col', y='ratio', hue='drought_type', data=data,kind='bar')        plt.figure(figsize=(8*centimeter_factor, 6*centimeter_factor))        sns.barplot(x='rs_col', y='ratio', hue='drought_type', data=data)        # plt.show()        # outf = join(outdir, 'rs_rt_area_ratio_bar.png')        outf = join(outdir, 'rs_rt_area_ratio_bar.pdf')        plt.savefig(outf, dpi=300)        plt.close()    def rs_rt_area_ratio_bar_two_regions(self):        outdir = join(self.this_class_png, 'rs_rt_area_ratio_bar_two_regions')        T.mk_dir(outdir)        df = Load_dataframe().load_chapter5()        threshold = 0.05        rs_cols = get_rs_rt_cols()        drought_type_list = global_drought_type_list        ELI_class_list = global_ELI_class_list        data = pd.DataFrame()        drt_list = []        rs_col_list = []        ratio_list = []        ELI_List = []        for drt in drought_type_list:            df_drt = df[df['drought_type'] == drt]            for ELI in ELI_class_list:                df_eli = df_drt[df_drt['ELI_class'] == ELI]                for rs_col in rs_cols:                    df_eli_copy = df_eli.copy()                    df_eli_copy = df_eli_copy.dropna(subset=['ELI', rs_col], how='any')                    vals = df_eli_copy[rs_col]                    vals = np.array(vals)                    vals = vals[vals < (1 - threshold)]                    # vals = vals[vals > (1 + threshold)]                    ratio = len(vals) / len(df_eli) * 100                    drt_list.append(drt)              